dump(_,<<>>) ->
  ok;

dump(Off,<<Bin:32/binary,Rest/binary>>) ->
  io:format("~4.16B    ~s~n",[Off,hex:encode(Bin)]),
  dump(Off+32,Rest);

dump(Off,<<Bin/binary>>) ->
  io:format("~4.16B   ~s~n",[Off,hex:encode(Bin)]),
  ok.



